  pub fn invoke(&self) -> solana_program_error::ProgramResult {
    self.invoke_signed(&[])
  }
  #[allow(clippy::clone_on_copy)]
  #[allow(clippy::vec_init_then_push)]
  pub fn invoke_signed(&self, signers_seeds: &[&[&[u8]]]) -> solana_program_error::ProgramResult {
          let args = ClaimFundsInstructionArgs {
                                                              market_id: self.instruction.market_id.clone().expect("market_id is not set"),
                                    };
        let instruction = ClaimFundsCpi {
        __program: self.instruction.__program,
                  
          user: self.instruction.user.expect("user is not set"),
                  
          market: self.instruction.market.expect("market is not set"),
                  
          user_stats: self.instruction.user_stats.expect("user_stats is not set"),
                  
          collateral_mint: self.instruction.collateral_mint.expect("collateral_mint is not set"),
                  
          outcome_yes_mint: self.instruction.outcome_yes_mint.expect("outcome_yes_mint is not set"),
                  
          outcome_no_mint: self.instruction.outcome_no_mint.expect("outcome_no_mint is not set"),
                  
          user_collateral: self.instruction.user_collateral.expect("user_collateral is not set"),
                  
          collateral_vault: self.instruction.collateral_vault.expect("collateral_vault is not set"),
                  
          user_outcome_yes: self.instruction.user_outcome_yes.expect("user_outcome_yes is not set"),
                  
          user_outcome_no: self.instruction.user_outcome_no.expect("user_outcome_no is not set"),
                  
          yes_escrow: self.instruction.yes_escrow.expect("yes_escrow is not set"),
                  
          no_escrow: self.instruction.no_escrow.expect("no_escrow is not set"),
                  
          associated_token_program: self.instruction.associated_token_program.expect("associated_token_program is not set"),
                  
          token_program: self.instruction.token_program.expect("token_program is not set"),
                  
          system_program: self.instruction.system_program.expect("system_program is not set"),
                          __args: args,
            };
    instruction.invoke_signed_with_remaining_accounts(signers_seeds, &self.instruction.__remaining_accounts)
  }
